(()=>{"use strict";document.addEventListener("DOMContentLoaded",(async function(){try{const s=await fetch("https://raw.githubusercontent.com/devchallenges-io/web-project-ideas/main/front-end-projects/data/property-listing-data.json"),n=await s.json(),a=document.querySelector(".cards-container");n.forEach((s=>{const n=(s=>{const{title:n,description:a,price:t,rating:e,superhost:c,capacity:r,image:i}=s,d=document.createElement("article");return d.classList.add("card"),d.innerHTML=`\n    <img\n      src="${i}"\n      alt="photo"\n      class="card-image"\n      loading="lazy"\n    />\n    ${c?'<div class="superhost-badge">Superhost ‚≠ê</div>':""}\n    <div class="card-content">\n      <h3 class="card-title">${n}</h3>\n      <p class="card-description">\n        ${a}\n      </p>\n      <div class="card-capacities">\n        <div class="card-bedrooms">\n          <img src="assets/images/house.svg" alt="bedroom">\n          <span>${r.bedroom} bedroom</span>\n        </div>\n        <div class="card-guests">\n          <img src="assets/images/user.svg" alt="guests">\n          <span>${r.people} guests</span>\n        </div>\n      </div>\n      <div class="card-footer">\n        <span class="price">$${t}<span class="duration">/night</span></span>\n        <span class="rating">\n          <img src="assets/images/star.svg" alt="rating">\n          <span>${e}</span>\n        </span>\n      </div>\n    </div>\n  `,d})(s);a.appendChild(n)}))}catch(s){console.error("Error loading cards:",s)}}))})();